from selenium import webdriver
from selenium.webdriver import Chrome
from selenium.webdriver.common.by import By
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as EC
from time import sleep
from json import load, dump
from PIL import Image, ImageEnhance, ImageFilter
import pytesseract
from random import randint
from data import *
member = data  # 身分證字號
# 設定 Tesseract OCR 執行檔的路徑
pytesseract.pytesseract.tesseract_cmd = r'C:\Program Files\Tesseract-OCR\tesseract.exe'
# 開啟chrome
driver = Chrome()
driver.maximize_window()
driver.get('https://game.mnd.gov.tw/gameindex.aspx')
count = len(member)
time = 0
# 刷題
while True:
    try:

        a = driver.find_element(By.CLASS_NAME, 'img-fluid.mt-sm-3')
        a.click()
        for i in range(9):
            # 開答案
            with open('imformation.json', 'r', encoding='UTF-8') as f:
                data = load(f)

            a = driver.find_element(
                By.CLASS_NAME, 'display-5.question2').text  # 抓題目
            answer = driver.find_elements(
                By.CLASS_NAME, 'btn-answer.btn-lg.btn-block')  # 抓選項
            if a in data:
                for i in answer:  # 選答案
                    if data[a] in i.text:
                        i.click()
                        a = driver.find_element(
                            By.CLASS_NAME, 'btn.btn-secondary.btn-lg-center.btn-block')
                        a.click()
                        break
            else:
                print(a)
                for i in answer:
                    print(i.text)
                t = int(input())-1
                with open('imformation.json', 'w', encoding='UTF-8') as f:
                    data[a] = answer[t].text
                    dump(data, f, ensure_ascii=False, indent=4)
                answer[t].click()
                a = driver.find_element(
                    By.CLASS_NAME, 'btn.btn-secondary.btn-lg-center.btn-block')
                a.click()

        # 處理 alert 確認框
        alert = driver.switch_to.alert
        alert.accept()

        # 輸入身分證字號
        a = driver.find_element(By.ID, 'PID')
        a.send_keys(member[time % count])
        print(member[time % count])

        is_end = False
        while not is_end:
            try:
                captcha_image = driver.find_element(By.ID, 'CAPTCHAImage')
                captcha_image.screenshot('captcha.png')

            except:
                sleep(1)
                driver.get('https://game.mnd.gov.tw/gameindex.aspx')
                data['次數'] += 1
                with open('imformation.json', 'w', encoding='UTF-8') as f:
                    dump(data, f, ensure_ascii=False, indent=4)
                is_end = True
                time += 1
                break

            # 圖片預處理
            image = Image.open('captcha.png')
            image = image.convert('L')  # 轉換為灰度圖像
            image = image.filter(ImageFilter.MedianFilter())  # 濾波去噪
            enhancer = ImageEnhance.Contrast(image)
            image = enhancer.enhance(2)  # 增強對比度
            image = image.point(lambda x: 0 if x < 140 else 255)  # 二值化

            # 保存預處理後的圖片
            image.save('captcha_processed.png')
            captcha_text = pytesseract.image_to_string(image)
            print(f'"{captcha_text}"', len(str(captcha_text)))
            if len(str(captcha_text)) == 7:
                captcha_input = driver.find_element(
                    By.ID, 'txtValidateCode')
                captcha_input.clear()
                captcha_input.send_keys(captcha_text)

                try:
                    submit_button = driver.find_element(
                        By.TAG_NAME, 'input[type="submit"]')
                except:
                    pass
                try:
                    submit_button.click()

                except:
                    sleep(2)
                    try:
                        alert = driver.switch_to.alert
                        if alert.text == '請稍後再試!':
                            sleep(10)
                        alert.accept()
                        sleep(0.5)
                    except:
                        pass
            else:
                a = driver.find_element(By.ID, 'reflash')
                a.click()
    # sleep(30)
    except:
        driver.get('https://game.mnd.gov.tw/gameindex.aspx')
